function[out]=thset(str)
%JLAB_SETTINGS  Specifies settings for customizable JLAB properties.
%
%   JLAB_SETTINGS specifies customizable properties, including: 
%  
%       custom linesytle types, for use with LINESTYLES
%       custom color schemes, for use with LINESTYLES
%       custom fontsize schemes, for use with FONTSIZE
%       directory names, for use with e.g. MATSAVE.
%    
%   Do not edit this file directly.  Instead, following the same format
%   as in this function, create an m-file script (not a function)
%   called USERSETTINGS.  If you edit this file, your custom settings
%   will be lost if you upgrade JLAB in the future.
%   __________________________________________________________________
%   This is part of JLAB --- type 'help jlab' for more information
%   (C) 2006--2011 J.M. Lilly --- type 'help jlab_license' for details
 

%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%
%
%DIRNAMES
%
%  User-specified direcory locations
% defaults
load db_path
dir.dat=[db_path '\MATLAB\data\'];
dir.fig=[db_path '\MATLAB\plot\'];
dir.dev=[db_path '\MATLAB\'];
% check machine & mass storage
if strcmp(db_path,'C:\Dropbox\Osci')
    disk = 'G:\';
elseif strcmp(db_path,'D:\Dropbox\NPhome')
    disk = 'D:\';
else
    disk = '';
end
    dir.temp=[disk '\TEMP\'];

%
% specific locations
dir.fimfig=[db_path '\NPhome\Fimbul\#PLOTS\'];
dir.fimdat=[db_path '\NPhome\Fimbul\#DATA\'];
dir.isromdat=[disk 'ISROMS_results\'];
dir.asfromfig=[db_path '\NPhome\Fimbul\#PLOTS\ISROMS_ASF\'];
dir.fisromfig=[db_path '\NPhome\Fimbul\#PLOTS\ISROMS_FIS\'];
dir.lilly=[db_path '\Lilly-Hattermann\'];
dir.home=[db_path];
%dir.fisromana=[db_path '\NPhome/Fimbul_data\#ISROMS\data\'];
%dir.fisromfrc=[db_path '\NPhome/Fimbul_data\#ISROMS\forcing\'];
% Fimbul data moved from dropbox:
dir.fisromana=['C:\THINF\#ISROMS\data\'];
dir.fisromfrc=['C:\THINF\#ISROMS\forcing\'];
dir.love=[db_path '/APN/projects/LoVe/'];


clear db_path

%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%



%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%
%LINESTYLES
%  User-specified fontsize sets for use with FONTSIZE
%    fonts.FORMAT= '[TITLE LABELS AXES TEXT]';
fonts.default=[14 12 12 12];
fonts.poster=[20 16 16 16];
fonts.transparency=[24 18 18 18];
fonts.jpofigure=[9 9 9 9];
%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%


%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%
%LINESTYLES
%
%  User-specified line style sets for use with LINESTYLE.
%
%    linestyles.name={'COLOR','STYLE','WIDTH'};
%
%  Line handles will be cycled through the available styles,
%  colors, and widths, so that S, C, and W need not have the 
%  same length as the number of line handles nor as each other.
%
%  An ellipsis may be used (for C only) to indicate a repeated
%  last color, thus 'kbgr...' is equivalent to 'krbrrrrrrr...'.
linestyles.default={'bgrcmyk','-',1};
linestyles.black={'k','-',1};
linestyles.just1={'bDDDDDD...','-',[2 1 1 1 1 1 1]};
linestyles.just2={'bgDDDDD...','-',[2 2 1 1 1 1 1]};
linestyles.just3={'bgrDDDD...','-',[2 2 2 1 1 1 1]};
linestyles.just4={'bgrcDDD...','-',[2 2 2 2 1 1 1]};
linestyles.just5={'bgrcmDD...','-',[2 2 2 2 2 1 1]};
linestyles.just6={'bgrcmyD...','-',[2 2 2 2 2 2 1]};
linestyles.groups4={'kkkkbbbbggggrrrr','-',1};
linestyles.groups5={'kkkkkbbbbbgggggrrrrr','-',1};
linestyles.groups6={'kkkkkkbbbbbbggggggrrrrrr','-',1};
linestyles.groups7={'kkkkkkkbbbbbbbgggggggrrrrrrr','-',1};
linestyles.groups8={'kkkkkkkkbbbbbbbbggggggggrrrrrrrr','-',1};
linestyles.groups9={'kkkkkkkkkbbbbbbbbbgggggggggrrrrrrrrr','-',1};
linestyles.groups10={'kkkkkkkkkkbbbbbbbbbbggggggggggrrrrrrrrrr','-',1};
linestyles.thick={'bgrcmyk','-',2};
linestyles.two={'Ek',['- ';'--'],[2 1]};
linestyles.three={'kEk',['--';'- ';'- '],[2 2 1]};
linestyles.four={'EkEk',['--';'--';'- ';'- '],[2 1 2 1]};
linestyles.just2bw={'kkkkkkk...','-',[2.5 2.5 1 1 1 1 1]};
linestyles.triplets={'bbbgggrrr',['- ';'--';'- '],[1 2 2]};
linestyles.redgreenblue={'rgb',['-';'-';'-'],[1 1 1]};
linestyles.dots={'bgrcmyk','.',1};
linestyles.graydots={'D','.',1};
%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%

%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%
%COLORS
%
%  User-specified additional colors for use with LINESTYLE.
colors.k=[0 0 0];
colors.w=[1 1 1];
colors.b=[0 0 1];
colors.L=[0 0 0.5];
colors.g=[0 1 0];
colors.M=[0 0.5 0];
colors.r=[1 0 0];
colors.N=[0.5 0 0];
%colors.c=[0 0.75 0.75];
colors.c=[0 1 1];
colors.m=[0.75 0 0.75];
colors.y=[0.75 0.75 0];
colors.A=(1-0)*[1 1 1];
colors.B=(1-1/10)*[1 1 1];
colors.C=(1-2/10)*[1 1 1];
colors.D=(1-3/10)*[1 1 1];
colors.E=(1-4/10)*[1 1 1];
colors.F=(1-5/10)*[1 1 1];
colors.G=(1-6/10)*[1 1 1];
colors.H=(1-7/10)*[1 1 1];
colors.I=(1-8/10)*[1 1 1];
colors.J=(1-9/10)*[1 1 1];
colors.K=(1-10/10)*[1 1 1];
%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%

figsave.png='-dpng';
figsave.eps='-deps';
figsave.ps='-dps';
figsave.epsc='-depsc';
figsave.psc='-dpsc';

figsave.orient.landscape='orient landscape';

if exist('usersettings')==2
    usersettings;
end

try 
    eval(['out=' str ';'])
catch
    out=[];
end

